package assignments;

public class assignment_25 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i=0;i<=100;i++)
		{
			if(i%2==0)
			{
				System.out.println("the number is even= "+i);
			}
			else
			{
				System.out.println("the number is odd= "+i);
			}
		}
		
	}

}
